mapper.select:
select sbi.studentNo as sbiStudentNo, sbi.studentName as sbiStudentName, sbi.birthday as birthday, sbi.className as className, sbi.major as major, sbi.college as college, sbi.sex as sex, sbi.nation as nation, sbi.hometown as hometown, sbi.political as political, sbi.idcard as idCard, sbi.telephone as telephone, sbi.email as email, sbi.emailOnly as emailOnly, sbi.family as family, sbi.address as address, sbi.fphone as fphone, sbi.photo as photo, sbi.eduBackground as eduBackground, sbi.englishlevel as englishlevel, sbi.bankcard as bankCard, sbi.remarks as sbiRemarks, sbi.exameState as sbiExameState, sbi.stuState as stuState, ai.actNo as aiActNo, ai.activityName as activityName, ai.activityType as activityType, ai.activityLevel as activityLevel, ai.sponsor as sponsor, ai.organize as organize, ai.teacherNo as teacherNo, ai.studentName as aiStudentName, ai.createTime as createTime, ai.info as info, ai.exameState as aiExameState, ai.remarks as aiRemarks, ai.actState as actState, sap.id as sapId, sap.studentNo as sapStudentNo, sap.actNo as sapActNo, sap.sessionYear as sessionYear, sap.awardTime as awardTime, sap.activityRating as sapActivityRating, sap.studentPost as studentPost, sap.workingTime as workingTime, sap.exameState as sapExameState, sap.remarks as sapRemarks, sap.stuActState as stuActState from stu_basic_info sbi, act_info ai, stu_act_participate sap where sbi.studentNo=sap.studentNo and sap.actno=ai.actno
and sap.exameState=#{exameState}
and sbi.major=#{major}
and sbi.sex=#{sex}
and ai.activityName=#{activityName}
and ai.activityLevel = #{activityLevel}
and ai.activityType = #{activityType}
and sap.studentPost=#{studentPost}
and sap.workingTime=#{workingTime}
and sap.sessionYear=#{sessionYear}
and sap.stuActState = #{stuActState}
and sap.awardTime = #{awardTime}
order by
#{item}
limit #{start},#{nums};

select sbi.studentNo as sbiStudentNo, sbi.studentName as sbiStudentName, sbi.birthday as birthday, sbi.className as className, sbi.major as major, sbi.college as college, sbi.sex as sex, sbi.nation as nation, sbi.hometown as hometown, sbi.political as political, sbi.idcard as idCard, sbi.telephone as telephone, sbi.email as email, sbi.emailOnly as emailOnly, sbi.family as family, sbi.address as address, sbi.fphone as fphone, sbi.photo as photo, sbi.eduBackground as eduBackground, sbi.englishlevel as englishlevel, sbi.bankcard as bankCard, sbi.remarks as sbiRemarks, sbi.exameState as sbiExameState, sbi.stuState as stuState, ai.actNo as aiActNo, ai.activityName as activityName, ai.activityType as activityType, ai.activityLevel as activityLevel, ai.sponsor as sponsor, ai.organize as organize, ai.teacherNo as teacherNo, ai.studentName as aiStudentName, ai.createTime as createTime, ai.info as info, ai.exameState as aiExameState, ai.remarks as aiRemarks, ai.actState as actState, sap.id as sapId, sap.studentNo as sapStudentNo, sap.actNo as sapActNo, sap.sessionYear as sessionYear, sap.awardTime as awardTime, sap.activityRating as sapActivityRating, sap.studentPost as studentPost, sap.workingTime as workingTime, sap.exameState as sapExameState, sap.remarks as sapRemarks, sap.stuActState as stuActState from stu_basic_info sbi, act_info ai, stu_act_participate sap where sbi.studentNo=sap.studentNo and sap.actno=ai.actno
and sbi.college=#{college}
and sbi.major=#{major}
and sbi.className=#{className}
and (sbi.studentName=#{stuNoOrName} or sbi.studentNo=#{stuNoOrName})
limit #{start},#{nums};
